import { platform } from "os";
import React from "react";

interface KnowledgeLevelProps {
    setLevel: (level: string) => void
}

interface AvailableHoursTimeProps {
    setHours: (hourse: number) => void
}

interface PlatformPreferedProps {
    setPlatform: (platform: string) => void
}

interface BudgetProps {
    setBudget: (budget: string) => void
}

interface AreaInterestsProps {
    setAreas: (areas: string[]) => void
}

interface FormProps {
    knowledgeLevel: string | null;
    availableHoursTime: number | null;
    platformPrefered: string | null;
    budget: string | null;
    areaInterests: string[] | null;
}

function ProfileFormDialog() {

    const [form, setForm] = React.useState<FormProps>({
        knowledgeLevel: null,
        availableHoursTime: null,
        platformPrefered: null,
        budget: null,
        areaInterests: null
    });

    return (
        <div className="
            fixed top-0 left-0 w-screen h-screen bg-gray-900/30 backdrop-blur-sm z-[100] grid place-items-center
            p-4 sm:p-8 lg:p-12
            "
        >
            <div className="bg-white w-full min-h-full rounded-lg p-6 relative overflow-hidden">
                <div 
                    className={`
                        bg-sky-600 absolute top-0 left-0 h-1.5
                        ${ form.knowledgeLevel !== null ? 'w-[20%]' : '' }
                        ${ form.availableHoursTime !== null ? 'w-[40%]' : '' }
                        ${ form.platformPrefered !== null ? 'w-[60%]' : '' }
                        ${ form.budget !== null ? 'w-[80%]' : '' }
                        ${ form.areaInterests !== null ? 'w-[100%]' : '' }
                    `} 
                />
                <button
                    className="absolute top-0 right-0 px-4 py-1.5 mt-4 mr-4 rounded-sm border border-gray-200 hover:bg-gray-50 transition-all duration-300 ease-in-out"
                >
                    Omitir
                </button>
                <div className="">
                    { form.knowledgeLevel === null ? (
                        <KnowledgeLevel 
                            setLevel={(level) => setForm({
                                ...form,
                                knowledgeLevel: level
                            })} 
                        />
                    ) : form.availableHoursTime === null ? (
                        <AvailableHoursTime 
                            setHours={(hours) => setForm({
                                ...form,
                                availableHoursTime: hours
                            })}
                        />
                    ) : form.platformPrefered === null ? (
                        <PlatformPrefered 
                            setPlatform={(platform) => setForm({
                                ...form,
                                platformPrefered: platform
                            })}
                        />
                    ) : form.budget === null ? (
                        <Budget 
                            setBudget={(budget) => setForm({
                                ...form,
                                budget: budget
                            })}
                        />
                    ) : form.areaInterests === null && (
                        <AreaInterests 
                            setAreas={(areas) => setForm({
                                ...form,
                                areaInterests: areas
                            })}
                        />
                    )}
                </div>
            </div>
        </div>
    );
}

